import React from 'react'
import { URL_Image } from '../URL';
import './homecard.css';
import { useHistory, Link, Switch, Redirect } from "react-router-dom";

export default function HomecardService({services}) {
    const history = useHistory();

    const redirect = ()=>{
        return   <Redirect  to={`${services.category.name.replace(/\s+/g, '-')+'/'+services.subcategory.name.replace(/\s+/g, '-')+'/'+services.title.replace(/\s+/g, '-')+'/'+services.id}`} />
    }

    return (
        <>

<div className="home-card">
    <div className="home-card-image-container">
  <img src={URL_Image+services.image} alt="Denim Jeans" className="home-card-image" />

  </div>
  {console.log(services)}

  <div className="home-card-info">
  <p className="home-title" onClick={()=>{
      redirect();
      console.log("entered")

    }}>{services.title}</p>
  <p className="home-price"><span className='home-span'>STARTING AT</span> ${services.price}</p>

  </div>
</div>
        </>

    )

}
