import React from 'react'
import { URL_Image } from '../URL';
import './homecard.css';
import { useHistory, Route, generatePath, Link } from "react-router-dom";

export default function HomecardService({services}) {
    const history = useHistory();
    const updatePath =()=>{
    const path = `${services.category.name.replace(/\s+/g, '-')+'/'+services.subcategory.name.replace(/\s+/g, '-')+'/'+services.title.replace(/\s+/g, '-')+'/'+services.id}`;
    history.push({
        pathname:path,
        search:'?color=blue'
    });
}

    return (
        <>

<div className="home-card">
    <div className="home-card-image-container">
  <img src={URL_Image+services.image} alt="Denim Jeans" className="home-card-image" />

  </div>
  {console.log(services)}

  <div className="home-card-info">

  <Link  className='home-a' onClick={()=>{


      updatePath()
    }} >
      <p className="home-title">{services.title}</p></Link>
  <p className="home-price"><span className='home-span'>STARTING AT</span> ${services.price}</p>

  </div>
</div>
        </>

    )

}
