import React,{useState,useEffect} from 'react';
import { useParams } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { URL_Image } from '../../components/URL';
import './service.css';
export default function Service() {
    const {id} = useParams();
    const  {  errorDataGuest,services } = useSelector((state) =>  state.fetchAllDataGuestReducer);
    const [service,Setservice]=useState('');
    const [height,setHeight] = useState(0);
    const [divElemnt,setDivElement] = useState();



   useEffect(()=>{
    services && services.map((ser)=>{

        if(id===ser.id.toString()){

           return Setservice(ser);

        }

       })

   })
   useEffect(() => {
    if(divElemnt){
        setHeight(divElemnt.clientHeight)
    }

}, [divElemnt])

    return (
        <>
        <div className='fixed-service'>
        <div className='service-container' ref={(heightRef)=>setDivElement(heightRef)}>

            <div className='service-left-inner'>
           <div className='service-title'> {service.title}</div>
           <div className='service-image'>
           <img src={URL_Image+service.image} alt={service.title} />
           </div>


            </div>
            <div className='service-right-inner'  style={{'margin-top':height/1.5}} >
                testing
            </div>      
        </div>
        </div>

        </>
    ) 
}
