import React,{useEffect,useState,useRef} from 'react'
import "./dropdown.css";
import { useSelector } from "react-redux";
export default function Dropdown() {
    const [open, setOpen] = useState(false);
    const container_dropdown = useRef(null);


    const  { guestData, errorDataGuest } = useSelector((state) =>  state.fetchAllDataGuestReducer);
    const handleClickOutside = event => {
      if (container_dropdown.current && !container_dropdown.current.contains(event.target)) {
        setOpen(false);
      }
    };

    useEffect(() => {
      document.addEventListener("mousedown", handleClickOutside);

      return () => {
        // clean up
        document.removeEventListener("mousedown", handleClickOutside);
      };
    });
    return (

        <div classNameName="containers-dropdown" ref={container_dropdown}   >

      {console.log('dropdown',guestData.guestData)}

        {guestData && guestData.guestData && guestData.guestData.map(guest=>(
        <button type="button" className="button-dropdown" onMouseEnter={() => setOpen(!open)} >
        {guest.name} hey
      </button>
       && open ? 

            <div className="dropdown-wrapper"  onMouseLeave={()=> setOpen(!open)} >
              <ul className="dropdown-menus" >

                <li className="dropdown-menu__item-dropdown">{guest.subcategories_accepted.name}</li>

              </ul>
          </div>:null

      ))} 

      {/* {open ? 

